                    outAmount,
                    i < routerId.length - 1 ? 0 : amountOutMin,
                    deadline
                );
            } else {
                IERC20(path[i][0]).approve(pairRouter, outAmount);
                amounts = IRequiemRouter02(pairRouter).swapExactTokensForTokens(
                        outAmount,
                        i < routerId.length - 1 ? 0 : amountOutMin,
                        path[i],
